#include <stdio.h>
#include <stdlib.h>
#include <string.h>
#include <stdbool.h>
#include <math.h>

void run(char puzzleInput[]) {
  int floor = 0;
  int i = 0;

  while(puzzleInput[i] != '\0') {
    if (puzzleInput[i] == '(') {
      floor += 1;
    } else if (puzzleInput[i] == ')') {
      floor -= 1;
      if (floor == -1) {
        printf("Basement instruction = %d\n", i + 1);
      }
    }

    i += 1;
  }

  printf("Answer = %d\n", floor);
}


